export const categoryState = {
  categories: [],
  addCategorymodel: false,
  editCategorymodel: {
    model: false,
    cId: null,
    des: "",
    status: "",
  },
  loading: false,
};

export const categoryReducer = (state, action) => {
  switch (action.type) {
    /* Get all category */
    case "fetchCategoryAndChangeState":
      return {
        ...state,
        categories: action.payload,
      };
    /* Create a category */
    case "addCategorymodel":
      return {
        ...state,
        addCategorymodel: action.payload,
      };
    /* Edit a category */
    case "editCategorymodelOpen":
      return {
        ...state,
        editCategorymodel: {
          model: true,
          cId: action.cId,
          des: action.des,
          status: action.status,
        },
      };
    case "editCategorymodelClose":
      return {
        ...state,
        editCategorymodel: {
          model: false,
          cId: null,
          des: "",
          status: "",
        },
      };
    case "loading":
      return {
        ...state,
        loading: action.payload,
      };
    default:
      return state;
  }
};
